value:
(props: EditorProps) => {
  const { value, onChange, withContext, className } = props;
  const contentRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!contentRef.current) return;
    contentRef.current.innerText = value;
  }, [value]);

  return (
    <Div
      contentEditable
      ref={contentRef}
      className={className}
      onPaste={() => {
        // parse all new words into a span per word
      }}
      onKeyPress={() => {
        const innerText = contentRef.current?.innerText || ""; //also innerHTML may be useful

        // IDK how to get this, research it
        const positionIndex = 0;

        const textEditingContext = getContext({
          text: innerText,
          positionIndex,
        });

        withContext?.(textEditingContext);

        onChange(innerText);
      }}
    />
  );
}
