value:
(props: {
  onChange: (value: string) => void;
  value: string;
  projectRelativeFilePath: string;
  markdownModelName?: keyof DbModels;
}) => {
  const { onChange, value, projectRelativeFilePath, markdownModelName } = props;
  const [lastIndex, setLastIndex] = useState(value.length);
  const [showAssetUpload, setShowAssetUpload] = useState(false);
  const toggleAssetUpload = () => setShowAssetUpload(!showAssetUpload);

  return (
    <Div className="w-full h-full flex flex-1 flex-col">
      <Div className="flex flex-row">
        <ClickableIcon
          emoji={showAssetUpload ? "❌" : "📂"}
          onClick={() => toggleAssetUpload()}
        />
        {showAssetUpload ? (
          <Div>
            <AssetInput
              config={{}}
              fieldName="upload"
              uniqueFieldId="upload"
              onChange={async (newBackendAssets) => {
                if (newBackendAssets.length === 0) return;

                // 1) upload to backend and process
                const apiResult = await api.processAssetUpload(
                  newBackendAssets
                );
                // 2) receive back the actual relativePath

                if (!apiResult.result) {
                  errorToast("Couldn't upload your assets");
                  return;
                }

                const assetsMarkdownString = apiResult.result
                  .map((backendAsset) => {
                    return `![${backendAsset.alt || ""}](${
                      backendAsset.relativePath
                    })`;
                  })
                  .join("\n");
                // 3) insert those as a snippet in the markdown
                // console.log({ assetsMarkdownString });

                const before = value.substring(0, lastIndex);
                const after = value.substring(lastIndex);
                const newText = `${before}${assetsMarkdownString}${after}`;
                onChange(newText);
                toggleAssetUpload();
              }}
              extra={{
                projectRelativeReferencingFilePath: projectRelativeFilePath,
                defaultAssetName: getFileOrFolderName(projectRelativeFilePath),
                allowMultiple: true,
                modelName: markdownModelName,
                size: "md",
                theme: "Outlined",
              }}
              value={[]}
            />
          </Div>
        ) : null}
      </Div>
      <ContextTextArea
        withContext={(context) => {
          setLastIndex(context.positionIndex);
          // console.log(`Context`, context);
        }}
        className="w-full bg-transparent flex flex-1"
        onChange={onChange}
        value={value}
      />
    </Div>
  );
}

