value:
(props: {
  modelName?: string;
  projectRelativeMarkdownFilePath: string;
  frontmatterSchema: SimplifiedSchema;
  markdownParse: MarkdownParse;
  onChange: (value: string) => void;
}) => {
  const {
    onChange,
    projectRelativeMarkdownFilePath,
    markdownParse,
    modelName,
    frontmatterSchema,
  } = props;
  const [immutableFrontmatterSchema] = useState<SimplifiedSchema>(
    props.frontmatterSchema
  );

  const referencableModelData = useReferencableModelData(
    immutableFrontmatterSchema
  );

  const uploadAssetsThenSetMarkdownString = async (parameters: any[]) => {
    const [frontmatter] = parameters;

    const assetParameters = findAssetParametersRecursively(frontmatter);

    // NB: because it's too complex and we don't need this in the case of frontmatter, we can just only look at the base level, because frontmatter has just the base level. We therefore skip any items with a stack.

    await Promise.all(
      assetParameters.map(async (assetParameter) => {
        if (assetParameter.stack) {
          console.log(
            "WARNING. Stack detected in assetParameters item, shouldn't happen for frontmatter"
          );
          return;
        }
        // 1. get the item
        const item = frontmatter[assetParameter.parameterName] as
          | BackendAsset
          | BackendAsset[]
          | undefined;

        const isCorrectType = typeof item === "object";
        const hasUploaded =
          item && isCorrectType
            ? !!makeArray(item).find((x) => x.relativePath)
            : false;
        // 2. process if not already
        const uploadedItem =
          item && isCorrectType && !hasUploaded
            ? (await api.processAssetUpload(makeArray(item))).result
            : item;

        // 3. return uploaded backend-asset

        const newAsset =
          !uploadedItem || !isCorrectType
            ? undefined
            : assetParameter.assetInputType.isMultiple
            ? makeArray(uploadedItem)
            : takeFirst(uploadedItem);

        frontmatter[assetParameter.parameterName] = newAsset;
      })
    );

    const realFrontmatter: Frontmatter = objectToFrontmatter(
      frontmatter,
      frontmatterSchema
    );

    const newMarkdownParse: MarkdownParse = {
      ...markdownParse,
      parameters: realFrontmatter,
    };

    const newMarkdownString = markdownParseToMarkdownString(newMarkdownParse);

    onChange(newMarkdownString);
  };

  const frontmatterParse = frontmatterToObject(
    markdownParse.parameters,
    frontmatterSchema
  );

  console.log({ frontmatterParse, frontmatter: markdownParse.parameters });

  return (
    <SimplifiedSchemaForm
      modelName={modelName}
      id={projectRelativeMarkdownFilePath}
      parameters={[
        {
          name: "Frontmatter",
          required: true,
          isDbModel: true,
          simplifiedSchema: immutableFrontmatterSchema,
        },
      ]}
      onChange={uploadAssetsThenSetMarkdownString}
      values={[frontmatterParse]}
      projectRelativeStorageFilePath={projectRelativeMarkdownFilePath}
      referencableModelData={referencableModelData}
    />
  );
}
