value:
(
  /**
   * Raw markdown string
   */
  markdownString: string,
  subtextConfig: SubtextConfig
): string | undefined => {
  if (!subtextConfig.isEnabled) return;

  const allSubtext = `${subtextConfig.translations?.join(
    ","
  )} and the text is '${markdownString}'. Let's hook it into translate, and hook up all stuff!`;

  return allSubtext;
}
