value:
(props: {
  view: "view" | "presentation";
  markdownParse: MarkdownParse;
  markdownParseRenderConfig: MarkdownParseRenderConfig;
}) => {
  const { markdownParse, markdownParseRenderConfig, view } = props;

  const {
    projectRelativeBaseFolderPath,
    projectRelativeMarkdownFilePath,
    augmentedWordObject,
  } = markdownParseRenderConfig;
  return (
    <>
      {view === "view" ? (
        <Div className="mx-2">
          {renderMarkdownParse(markdownParse, markdownParseRenderConfig)}
        </Div>
      ) : null}

      {view === "presentation" ? (
        <Div className="mx-2 flex flex-1 min-h-[80vh]">
          {markdownParse.content ? (
            <MarkdownParsePresentation
              markdownParse={markdownParse}
              augmentedWordObject={augmentedWordObject}
              projectRelativeBaseFolderPath={projectRelativeBaseFolderPath}
              projectRelativeMarkdownFilePath={projectRelativeMarkdownFilePath}
            />
          ) : (
            "Couldn't find any content"
          )}
        </Div>
      ) : null}
    </>
  );
}
