value:
(props: {
  /**
   * raw markdown that should be converted into a subtext
   */
  rawMarkdown: string;
  subtextConfig: SubtextConfig;
}) => {
  const { rawMarkdown, subtextConfig } = props;

  if (!subtextConfig.isEnabled) return null;
  const subtext = getSubtext(rawMarkdown, subtextConfig);
  return (
    <div
      contentEditable={false}
      spellCheck={false}
      autoCorrect="false"
      data-is-augmentation="true"
      style={{ fontSize: 8, backgroundColor: "#CCC", padding: 5 }}
    >
      {subtext}
    </div>
  );
}
