value:
(props: {
  context: TextEditingContext | null;
  augmentedWords?: AugmentedWord[];
  augmentedWordObject?: MappedObject<AugmentedWord>;
}) => {
  const { augmentedWords, context, augmentedWordObject } = props;

  const filteredAugmentedWords =
    augmentedWords?.filter((x) =>
      ["operation", "bundle", "type", "function", "variable"].includes(x.type)
    ) || [];

  const completableWord = context?.wordAtPosition;

  const completions = completableWord
    ? filteredAugmentedWords
        .filter((augmentedWord) => {
          const isMatch = isAugmentedWordMatch(augmentedWord, completableWord);

          return isMatch;
        })
        .slice(0, MAX_COMPLETIONS_AMOUNT)
    : [];

  return (
    <Div>
      {completions.map((completion, index) => {
        return (
          <Completion
            augmentedWordObject={augmentedWordObject}
            augmentedWord={completion}
            key={`completion${index}`}
          />
        );
      })}
    </Div>
  );
}
