() => {
  const [subtextConfig, setSubtextConfig] = useStore("subtextConfig");
  const [subwordConfig, setSubwordConfig] = useStore("subwordConfig");

  const values: WriterConfigFormValue = [subtextConfig, subwordConfig];

  return (
    <Div>
      <SimplifiedSchemaForm
        id="writerConfig"
        onChange={(values) => {
          const [newSubtextConfig, newSubwordConfig] =
            values as WriterConfigFormValue;
          setSubtextConfig(newSubtextConfig);
          setSubwordConfig(newSubwordConfig);
        }}
        parameters={[
          {
            name: "SubtextConfig",
            required: true,
            isDbModel: false,
            simplifiedSchema: SubtextConfigIndex.type
              .simplifiedSchema as SimplifiedSchema,
          },
          {
            name: "SubwordConfig",
            required: true,
            isDbModel: false,
            simplifiedSchema: SubwordConfigIndex.type
              .simplifiedSchema as SimplifiedSchema,
          },
        ]}
        values={values}
      />
    </Div>
  );
}
